import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { redirect } from "react-router-dom";
import './login.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faUser } from '@fortawesome/free-solid-svg-icons';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
    
  } from "react-router-dom";

function Login() {
    const [formData, setFormData] = useState({});

    const [isLoggedIn, setLoggedIn] = useState(false);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const requestOptions = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData)
        };

        fetch('http://localhost:8000/token/', requestOptions)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                let token = "Token " + data.token;
                localStorage.setItem('token', token);
                setLoggedIn(true);
                redirect("/song");
                window.location.reload();
            })
            .catch(error => {
                console.error('Error:', error);
            });


    };

    return (
        <Container className='log-cont'>
            <div className='login-card'>
                <div>
                    
                    <a href='#signin' className='btn btn-log'>
                        <FontAwesomeIcon icon={faPlus}></FontAwesomeIcon>
                    </a>
                </div>
                <div className='login' id='login'>
                    <form onSubmit={handleSubmit}>
                        <div className="form-group mb-3 mt-3">
                            <input
                                type="text"
                                name="username"
                                className="form-control"
                                placeholder="hell"
                                value={formData.username || ''}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group mb-3">
                            <input
                                type="password"
                                name="password"
                                className="form-control"
                                placeholder="Password"
                                value={formData.password || ''}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <button
                                type="submit"
                                className="form-control btn btn-outline-light rounded submit px-3">
                                Sign In
                            </button>
                        </div>
                    </form>
                </div>
                <div className='signin' id='signin'>
                    <form onSubmit={handleSubmit}>
                        <div className="form-group mb-3 mt-3">
                            <input
                                type="text"
                                name="username"
                                className="form-control"
                                placeholder="helloJSDHfkSJDn"
                                value={formData.username || ''}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group mb-3">
                            <input
                                type="password"
                                name="password"
                                className="form-control"
                                placeholder="Password"
                                value={formData.password || ''}
                                onChange={handleInputChange}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <button
                                type="submit"
                                className="form-control btn btn-outline-light rounded submit px-3">
                                Sign In
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </Container>
    );
}

export default Login;
