import React, { useState, useEffect } from "react";
import './playlist.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlay } from "@fortawesome/free-solid-svg-icons";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
  } from "react-router-dom";

function Playlist() {

    const [playlist, setPlaylist] = useState([]);
    const fetchPlaylist = async () => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/playlist/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setPlaylist(data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchPlaylist()
    }, []);

    return (
        <div>
            <Row className="row">
                {playlist.map((play, index) => (
                    <Col lg="4">
                        <Card key={index} style={{ width: '18rem' }} className="playlist-card m-4">
                            <Card.Body>
                                <Card.Img variant="top" src="https://images.unsplash.com/photo-1581974944026-5d6ed762f617?q=80&w=1470&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" />
                                <div className="row mt-2">
                                    <Card.Title className="col-8">{play.playlist_name}</Card.Title>
                                    <Link to="/playlistdetail/$" className="col-4 play-button" variant="btn">
                                        <FontAwesomeIcon size="2x" className="play-icon" icon={faCirclePlay}></FontAwesomeIcon>
                                    </Link>
                                </div>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>


        </div>
    )
}


export default Playlist
