import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './songs.css';
import Songdetail from '../songdetail/songdetail.js';


function Song() {
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    return (
        <div>
            {songs.map((song, index) => (
                <Card key={index} className="mt-4 card" onClick={() => window.location.href = '/songdetail'}>
                    <Songdetail id={song.id}/>
                    <div className="d-flex">
                        <Card.Img variant="top" className="song-image rounded" src={song.image} />
                        <Card.Body>
                            <Card.Title>{song.name}</Card.Title>
                        </Card.Body>
                    </div>
                </Card>
            ))}
        </div>
    );
}

export default Song;
