import React, { useState, useEffect } from "react";
import './search.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPlay } from "@fortawesome/free-solid-svg-icons";
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';

function Search() {
    const [songs, setSongs] = useState([]);
    const [results, setResult] = useState([]);
    const [searchsong, setSearch] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    const handleClick = (id) => {
        setSearch(id)
    }

    const handleChange = (e) => {
        const pattern = e.target.value;
        const filteredSongs = songs.filter(item => item.name.toLowerCase().includes(pattern.toLowerCase()));
        setResult(filteredSongs);
    };



    return (
        <div>
            <div className="body">
                <div className="wrap">
                    <div className="search mt-3">
                        <input type="text" onChange={handleChange} className="searchTerm" placeholder="What are you looking for?" />
                        <button type="submit" className="searchButton">
                            <FontAwesomeIcon icon={faSearch}></FontAwesomeIcon>
                        </button>
                    </div>
                    <ListGroup show={results.length > 0} className="drop-menu mt-2">
                        {results.map((result, index) => (
                            <ListGroup.Item onClick={() => handleClick(result)} key={index} className="text-white menu-item">{result.name}</ListGroup.Item>
                        ))}
                    </ListGroup>
                </div>
            </div>
            <Card>
                <img src={searchsong.image} alt="" />
            </Card>
        </div>
    );
}

export default Search;
