import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import { Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faHeart, faMusic, faPlay, faSearch } from '@fortawesome/free-solid-svg-icons';
import { faHome } from '@fortawesome/free-solid-svg-icons';
import Login from '../login/login';
import Song from '../songs/songs';
import Songdetail from '../songdetail/songdetail';

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
} from "react-router-dom";

import './navbar.css';
function NavBar() {


  const [songs, setSongs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        let header = new Headers();
        header.append("Content-type", 'application/json');
        let token = localStorage.getItem('token');
        if (token) {
          header.append('Authorization', token);
        }
        const requestOptions = {
          method: 'GET',
          headers: header
        };

        const response = await fetch('http://localhost:8000/song/', requestOptions);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setSongs(data);
        console.log(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };
    fetchData();
  }, []);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  return (
    <div>
      <Router>
        <Navbar expand="lg" className="nav">
          <Navbar.Brand href="#home" className='ms-5 text-white'><FontAwesomeIcon className='me-3' icon={faMusic}></FontAwesomeIcon><strong>Musicia</strong></Navbar.Brand>
          <Navbar.Toggle className='toggle'>
            <Button variant='btn' onClick={handleShow}>
              <FontAwesomeIcon icon={faBars}></FontAwesomeIcon>
            </Button>
          </Navbar.Toggle>
        </Navbar>
        <Row className='main'>
          <Col lg="4" className="d-none d-lg-block">
            <nav id="sidebar" className='rounded mt-3 ms-3'>
              <ul className="list-unstyled components ms-3">
                <li className="active">
                  <Link to="/song"><FontAwesomeIcon className='me-3' icon={faHome}></FontAwesomeIcon><strong>Home</strong></Link>
                </li>
                <li className="active">
                  <Link><FontAwesomeIcon className='me-3' icon={faSearch}></FontAwesomeIcon><strong>Search</strong></Link>
                </li>
              </ul>
            </nav>
            <nav id="sidebar" className='rounded mt-3 ms-3 nav2'>
              <ul className="list-unstyled components ms-3">
                <li>
                  <a><FontAwesomeIcon className='me-3' icon={faPlay}></FontAwesomeIcon><strong>Playlists</strong></a>
                </li>
                <li>
                  <a href="#"><FontAwesomeIcon className='me-3' icon={faHeart}></FontAwesomeIcon><strong>Liked</strong></a>
                </li>
                <li className='mt-auto'>
                  <Link to="/login">Login</Link>
                </li>
              </ul>
            </nav>
          </Col>
          <Col lg="8" className='rounded cont mt-3'>
            <Routes>
              <Route
                path="/song"
                element={<Song />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/login"
                element={<Login />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/songdetail/:id"
                element={<Songdetail />}
              ></Route>
            </Routes>

          </Col>
        </Row>
        <div className='play fixed-bottom'>
          <Row>
            <Col lg="4">
            </Col>
            <Col lg="4">
              {/* <tbody>
                {songs.map((song, index) => (
                  <tr key={index} className="list-row">
                    
                    <td>
                      <img className="table-image rounded" src={song.image} />
                      <span className="ms-2">{song.name}</span>
                    </td>
                    <td>{song.artist_name}</td>
                    
                  </tr>
                ))}
              </tbody> */}
              {songs[0].song_file && (
                <audio controls={true} autoPlay>
                  <source src={songs[0].song_file} />
                </audio>
              )}

            </Col>
            <Col lg="4">
            </Col>
          </Row>
        </div>

        <Offcanvas show={show} onHide={handleClose}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Musicia</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body className='off'>
            <nav id="sidebar" className='border-end'>
              <ul className="list-unstyled components ms-5">
                <li className="active">
                  <Link to="/song">Songs</Link>
                </li>
                <li>
                  <a>Playlists</a>
                </li>
                <li>
                  <a href="#">Liked</a>
                </li>
                <li>
                  <Link to="/login">Login</Link>
                </li>
              </ul>
            </nav>
          </Offcanvas.Body>
        </Offcanvas>
      </Router>
    </div>

  );
}

export default NavBar;
