import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay } from "@fortawesome/free-solid-svg-icons";
import Card from 'react-bootstrap/Card';
import Table from 'react-bootstrap/Table';
import './songs.css';
import SongDetail from '../songdetail/songdetail.js';

function Song(props) {
    const [songs, setSongs] = useState([]);
    const [songId, setSongId] = useState(null);
    const [hoveredIndex, setHoveredIndex] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    function handleLoadedMetadata(e, index) {
        const audio = e.target;
        const duration = audio.duration;
        const durationCell = document.getElementById(`duration-${index}`);
        durationCell.textContent = formatDuration(duration);
    }

    function formatDuration(duration) {
        const minutes = Math.floor(duration / 60);
        const seconds = Math.floor(duration % 60);
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    }

    const handleMouseEnter = (index) => {
        setHoveredIndex(index);
    };

    const handleMouseLeave = () => {
        setHoveredIndex(null);
    };

    const handleClick = (id) => {
        setSongId(id);
    }

    return (
        <div>
            <Card className="card">
                <Table className="table">
                    <thead className="fixed">
                        <tr>
                            <th>#</th>
                            <th>Title</th>
                            <th>Artist</th>
                            <th>Duration</th>
                        </tr>
                    </thead>
                    <tbody>
                        {songs.map((song, index) => (
                            <tr key={index} className="list-row">
                                <td className="index" onMouseEnter={() => handleMouseEnter(index)} onMouseLeave={handleMouseLeave}>
                                    <span className={`mt-5 number ${hoveredIndex === index ? 'hidden' : ''}`}>{index + 1}</span>
                                    <span onClick={() => handleClick(song.id)} className={`mt-5 play-button ${hoveredIndex !== index ? 'hidden' : ''}`}>
                                        <FontAwesomeIcon className='me-3' icon={faPlay}></FontAwesomeIcon>
                                    </span>
                                </td>

                                <td>
                                    <img className="table-image rounded" src={song.image} />
                                    <span className="ms-2">{song.name}</span>
                                </td>
                                <td>{song.artist_name}</td>
                                <td id={`duration-${index}`}>Loading...</td>
                                <td style={{ display: "none" }}>
                                    <audio controls onLoadedMetadata={e => handleLoadedMetadata(e, index)}>
                                        <source src={song.song_file} type="audio/mpeg" />
                                        Your browser does not support the audio element.
                                    </audio>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Card>
            {songId && <SongDetail songId={songId} />}
        </div>
    );
}

export default Song;

function SongD(props) {
    const [song, setSong] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch(`http://localhost:8000/song/${props.songId}/`, requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSong(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        if (props.songId) {
            fetchData();
        }
    }, [props.songId]);

    return(
        <div className="song">
            {song && (
                <div className="audio-player">
                    <audio controls autoPlay>
                        <source src={song.song_file} />
                    </audio>
                </div>
            )}
        </div>
    );
}
