import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import { Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import Login from '../login/login';
import { Outlet, Link } from "react-router-dom";

import './navbar.css';
function NavBar() {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);



  return (
    <div>
      <Navbar expand="lg" className="nav">
        <Navbar.Brand href="#home" className='ms-5 text-white'>Musicia</Navbar.Brand>
        <Navbar.Toggle className='toggle'>
          <Button variant='btn' onClick={handleShow}>
            <FontAwesomeIcon icon={faBars}></FontAwesomeIcon>
          </Button>
        </Navbar.Toggle>
      </Navbar>
      <Row className='main'>
        <Col lg="2" className="d-none d-lg-block">
          <nav id="sidebar" className='border-end'>
            <ul className="list-unstyled components ms-5">
              <li className="active">
                <a>Songs</a>
              </li>
              <li>
                <a href="#">Playlists</a>
              </li>
              <li>
                <a href="#">Liked</a>
              </li>
              <Link to=''>
                <a href="#">Login</a>
              </Link>
            </ul>
          </nav>
        </Col>
        <Col lg="10" className='cont'>
          main
        </Col>
      </Row>
      <Offcanvas show={show} onHide={handleClose}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Musicia</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body className='off'>
          <nav id="sidebar">
            <ul className="list-unstyled components">
              <li class="active">
                <a>Songs</a>
              </li>
              <li>
                <a href="#">Playlists</a>
              </li>
              <li>
                <a href="#">Liked</a>
              </li>
            </ul>
          </nav>
        </Offcanvas.Body>
      </Offcanvas>

    </div>

  );
}

export default NavBar;
