import React, { useState, useEffect } from "react";
import './songdetail.css';
import Container from 'react-bootstrap/Container';

function Songdetail({ id }) {
    const [isPlaying, setIsPlaying] = useState(false);
    const [song, setSong] = useState({});

    useEffect(() => {
        GetSong(id); // Call GetSong function with the provided id
    }, [id]); // Re-run the effect whenever id changes

    function GetSong(id) {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch(`http://localhost:8000/song/${id}`, requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSong(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }

    const togglePlay = () => {
        setIsPlaying(prevState => !prevState);
    };

    return (
        <div>
            <Container className="container">
                {song.song_file && (
                    <audio controls={true} autoPlay>
                        <source src={song.song_file} />
                    </audio>
                )}
            </Container>
        </div>
    );
}

export default Songdetail;
