import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './songdetail.css';

function Songdetail() {
    const { id } = useParams();
    const [isPlaying, setIsPlaying] = useState(false);
    const [song, setSong] = useState({});

    const togglePlay = () => {
        setIsPlaying(prevState => !prevState);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch(`http://localhost:8000/song/${id}/`, requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSong(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, [id]); // Add id to the dependency array to re-fetch data when id changes

    return (
        <div className="container d-flex justify-content-center my-4 mb-5">
            <div id="mobile-box">
                <div className="card">
                    <div className="bg-image ripple" data-mdb-ripple-color="light">
                        <img className="card-img-top rounded" src={song.image} alt="Song" />
                        <a href="#!">
                            <div className="mask" style={{ backgroundColor: 'rgba(251, 251, 251, 0.15)' }}></div>
                        </a>
                    </div>
                    <h1>{song}</h1>
                    <div className="card-body text-center">
                        <h5 className="h5 font-weight-bold"><a href="#" target="_blank" rel="noreferrer">{song.artist_name}</a></h5>
                        <p className="mb-0">{song.name}</p>
                        <audio controls={true}>
                            <source src={song.song_file} />
                        </audio>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Songdetail;
