import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { redirect } from "react-router-dom";
import './login.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faUser } from '@fortawesome/free-solid-svg-icons';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,

} from "react-router-dom";

function Login() {
    const [formData, setFormData] = useState({});

    const [isLoggedIn, setLoggedIn] = useState(false);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const requestOptions = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData)
        };

        fetch('http://localhost:8000/token/', requestOptions)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                let token = "Token " + data.token;
                localStorage.setItem('token', token);
                setLoggedIn(true);
                redirect("/song");
                window.location.reload();
            })
            .catch(error => {
                console.error('Error:', error);
            });


    };

    const [isSignupActive, setIsSignupActive] = useState(false);

    const handleImgBtnClick = () => {
        setIsSignupActive(prevState => !prevState);
    };

    return (
        <Container className='log-cont'>
            <form>
                <div id="login" class="log rounded">
                    <i class="fa-solid fa-gears fa-2x"></i>
                    <h3>LOGIN FORM</h3>
                    <input class="input rounded" type="email" placeholder="email" />
                    <input class="input rounded" type="password" placeholder="password" />
                    <button class="login btn btn-outline-light ms-2">Login <i class="fa-solid fa-right-to-bracket"></i></button>
                    <p class="note">dont have an account?<a href="#signup">signup</a></p>
                </div>
            </form>
            <div id="signup" class="log1 rounded">
                <i class="fa-solid fa-gears fa-2x"></i>
                <h3>SIGN-UP FORM</h3>
                <form>
                    <input class="input" type="email" placeholder="email" />
                    <input class="input" type="text" placeholder="name" />
                    <input id="pass" class="input" type="text" placeholder="password" />
                    <input id="rpass" class="input" type="password" placeholder="repeat password" />
                    <center>
                        <button class="login">Create Account <i class="fa-solid fa-right-to-bracket"></i></button>
                    </center>
                    <p class="note">already have an account?<a href="#login">login</a></p>
                </form>
            </div>

        </Container>
    );
}

export default Login;
