import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './songs.css';
import Songdetail from '../songdetail/songdetail.js';

function Song() {
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    return (
        <div>
            {songs.map((song, index) => (
                <Card key={index} className="mt-4 card">
                    <Link to={`/songdetail/${song.id}`} className="card-link">
                        <div className="d-flex">
                            <Card.Img variant="top" className="song-image rounded" src={song.image} />
                            <Card.Body>
                                <Card.Title>{song.name}</Card.Title>
                                <audio controls>
                                    <source src={song.song_file} />
                                </audio>
                            </Card.Body>
                        </div>
                    </Link>
                </Card>
            ))}
        </div>
    );
}

export default Song;
