                  {token ? (
                    <div className='d-flex'>
                    <svg width="100" height="100">
                      <circle cx="60" cy="40" r="25" fill="#cf6679" />
                      <text x="60" y="40" font-family="Arial" font-size="24" fill="white" text-anchor="middle" alignment-baseline="middle">{user.first_name[0]}</text>
