            <Routes>
              <Route
                path="/search"
                element={<Search />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/playlist"
                element={<Playlist />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/playlistdetail/:id"
                element={<Playlistdetail sendData={sendData} />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/liked"
