                </li>
                <li className='mt-auto' onClick={() => handleClose()}>
                  <Link to="/login">Login</Link>
                </li>
              </ul>
            </nav>
          </Offcanvas.Body>
        </Offcanvas>
      </Router>
    </div>

  );
