import React, { useState, useEffect } from "react";
import './playlist.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';

function Playlist() {

    const [playlist, setPlaylist] = useState([]);
    const fetchPlaylist = async () => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/playlist/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setPlaylist(data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchPlaylist()
    }, []);

    return (
        <div>
            {playlist.map((play, index) => (
                <Col>
                    <Card style={{ width: '18rem' }} className="card">
                        <Card.Body>
                            <Card.Img variant="top" src="https://images.unsplash.com/photo-1581974944026-5d6ed762f617?q=80&w=1470&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" />
                            <Card.Title>Card Title</Card.Title>
                            <Card.Text>
                                Some quick example text to build on the card title and make up the
                                bulk of the card's content.
                            </Card.Text>
                            <Button variant="primary">Go somewhere</Button>
                        </Card.Body>
                    </Card>
                </Col>
            ))}


        </div>
    )
}


export default Playlist
