import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './songs.css';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Songdetail from '../songdetail/songdetail.js';
import { faPlay } from "@fortawesome/free-solid-svg-icons";
import {
    BrowserRouter as Router,
    Routes,
    Route,
} from "react-router-dom";


function Song() {
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);


    function handleLoadedMetadata(e, index) {
        const audio = e.target;
        const duration = audio.duration;
        const durationCell = document.getElementById(`duration-${index}`);
        durationCell.textContent = formatDuration(duration);
    }

    function formatDuration(duration) {
        const minutes = Math.floor(duration / 60);
        const seconds = Math.floor(duration % 60);
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    }

    const [hoveredIndex, setHoveredIndex] = useState(null);

    const handleMouseEnter = (index) => {
        setHoveredIndex(index);
    };

    const handleMouseLeave = () => {
        setHoveredIndex(null);
    };

    const [valueToPass, setValueToPass] = useState('');

    const handleButtonClick = (id) => {
        setValueToPass(id);
    };




    return (
        <div>
            <Card className="card">
                <table className="table">
                    <thead className="fixed">
                        <tr>
                            <th>#</th>
                            <th>Title</th>
                            <th>Artist</th>
                            <th>Duration</th>
                        </tr>
                    </thead>
                    <tbody>
                        {songs.map((song, index) => (
                            <tr key={index} className="list-row">
                                <td className="index" onMouseEnter={() => handleMouseEnter(index)} onMouseLeave={handleMouseLeave}>
                                    <span className={`mt-5 number ${hoveredIndex === index ? 'hidden' : ''}`}>{index + 1}</span>
                                    {/* <Link to={`/songdetail/${song.id}`}><span className={`mt-5 play-button ${hoveredIndex !== index ? 'hidden' : ''}`}><FontAwesomeIcon className='me-3' icon={faPlay}></FontAwesomeIcon></span></Link> */}
                                    <button onClick={() => handleButtonClick(song.id)}></button>
                                    {valueToPass && <Nav propFromParent={valueToPass} />}
                                </td>
                                <td>
                                    <img className="table-image rounded" src={song.image} />
                                    <span className="ms-2">{song.name}</span>
                                </td>
                                <td>{song.artist_name}</td>
                                <td id={`duration-${index}`}>Loading...</td>
                                <td style={{ display: "none" }}>
                                    <audio controls onLoadedMetadata={e => handleLoadedMetadata(e, index)}>
                                        <source src={song.song_file} type="audio/mpeg" />
                                        Your browser does not support the audio element.
                                    </audio>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div>
                    <Routes>
                        <Route
                            path="/songdetail/:id"
                            element={<Songdetail />}
                        ></Route>
                    </Routes>
                </div>
            </Card>

        </div>
    );
}

export default Song;
