import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './songdetail.css';
import { Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faHeart, faPlus } from "@fortawesome/free-solid-svg-icons";
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

function Songdetail({ idtopass }) {
    const [isPlaying, setIsPlaying] = useState(false);
    const [song, setSong] = useState({});

    const fetchDataDetail = async (id) => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/song/${id}/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setSong(data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchDataDetail(idtopass);
        setIsPlaying(false); // Stop playing when new id is received
    }, [idtopass]);

    const togglePlay = () => {
        setIsPlaying(!isPlaying);
    };

    return (
        <div>
            <Row className="play-div">
                <Col lg="4" className="song-title">
                    <img src={song.image} className="song-image rounded ms-2" alt="" />
                    <h3 className="title ms-2 text-white">{song.name}</h3>
                </Col>
                <Col lg="4">
                    <div className="song">
                        {song.song_file && (
                            <div className="audio-player">
                                <audio controls={true} autoPlay={isPlaying}>
                                    <source src={song.song_file} />
                                </audio>
                            </div>
                        )}
                    </div>
                </Col>
                <Col lg="4" className="text-end song-button">
                    <div className="me-5">
                        <OverlayTrigger
                            placement="top"
                            delay={{ show: 250, hide: 400 }}
                            overlay={<Tooltip id="button-tooltip">like</Tooltip>}
                        >
                            <button className="icon-button btn"><FontAwesomeIcon className="icon m-3" icon={faHeart}></FontAwesomeIcon></button>
                        </OverlayTrigger>
                        <OverlayTrigger
                            placement="top"
                            delay={{ show: 250, hide: 400 }}
                            overlay={<Tooltip id="button-tooltip">add to playlist</Tooltip>}
                        >
                            <button className="icon-button btn"><FontAwesomeIcon className="icon m-3" icon={faPlus}></FontAwesomeIcon></button>
                        </OverlayTrigger>
                        <OverlayTrigger
                            placement="top"
                            delay={{ show: 250, hide: 400 }}
                            overlay={<Tooltip id="button-tooltip">more options</Tooltip>}
                        >
                            <button className="icon-button btn"><FontAwesomeIcon className="icon m-3" icon={faBars}></FontAwesomeIcon></button>
                        </OverlayTrigger>
                    </div>
                </Col>
            </Row>
        </div>
    );
}

export default Songdetail;
