import React, { useState, useEffect } from "react";
import './playlist.css';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import { Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlay, faPlus } from "@fortawesome/free-solid-svg-icons";
import Collapse from 'react-bootstrap/Collapse';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
} from "react-router-dom";

function Playlist() {

    const [open, setOpen] = useState(false);

    const [playlist, setPlaylist] = useState([]);
    const fetchPlaylist = async () => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/playlist/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setPlaylist(data);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    useEffect(() => {
        fetchPlaylist()
    }, []);

    const createPlaylist = async () => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'POST',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/playlist/createplaylist/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            window.location.reload();
        } catch (error) {
            console.error('Error:', error);
        }
    }

    return (
        <div>
            <div className="p-3 text-end">
                <input type="text" className="playlist-name-input rounded me-4" />
                button
                <FontAwesomeIcon icon={faPlus} onClick={() => createPlaylist()} className="icon"></FontAwesomeIcon>
            </div>
            <Row className="row">
                {playlist.map((play, index) => (
                    <Col lg="4">
                        <Card key={index} style={{ width: '18rem', position: 'relative' }} className="playlist-card m-4">
                            <div style={{ position: 'relative' }} className="card-bdy rounded">
                                <div className="overlay d-flex justify-content-between align-items-center position-absolute w-100" style={{ bottom: '0', padding: '10px', color: 'white' }}>
                                    <span><h2>{play.playlist_name}</h2></span>
                                    <Link to={`/playlistdetail/${play.id}`} className="play-button" variant="btn">
                                        <FontAwesomeIcon size="2x" className="play-icon" icon={faCirclePlay}></FontAwesomeIcon>
                                    </Link>
                                </div>
                            </div>
                        </Card>
                    </Col>
                ))}
            </Row>


        </div>
    )
}


export default Playlist
