import React, { useState, useEffect } from "react";
import './search.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPlay } from "@fortawesome/free-solid-svg-icons";
import Dropdown from 'react-bootstrap/Dropdown';

function Search() {
    const [songs, setSongs] = useState([]);
    const [results, setResult] = useState([]);
    const [hoveredIndex, setHoveredIndex] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch('http://localhost:8000/song/', requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData();
    }, []);

    const handleChange = (e) => {
        const pattern = e.target.value;
        const filteredSongs = songs.filter(item => item.name.toLowerCase().includes(pattern.toLowerCase()));
        setResult(filteredSongs);
    };

    const handleLoadedMetadata = (e, index) => {
        const audio = e.target;
        const duration = audio.duration;
        const durationCell = document.getElementById(`duration-${index}`);
        durationCell.textContent = formatDuration(duration);
    };

    const formatDuration = (duration) => {
        const minutes = Math.floor(duration / 60);
        const seconds = Math.floor(duration % 60);
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    };

    const handleMouseEnter = (index) => {
        setHoveredIndex(index);
    };

    const handleMouseLeave = () => {
        setHoveredIndex(null);
    };

    const handleClick = (id) => {
        localStorage.setItem('id', id);
    };

    return (
        <div>
            <div className="body">
                <div className="wrap">
                    <div className="search mt-3">
                        <input type="text" onChange={handleChange} className="searchTerm" placeholder="What are you looking for?" />
                        <button type="submit" className="searchButton">
                            <FontAwesomeIcon icon={faSearch}></FontAwesomeIcon>
                        </button>
                    </div>
                    <Dropdown>
                        <Dropdown.Menu show={results.length > 0} className="drop-menu mt-2">
                            {results.map((result, index) => (
                                <Dropdown.Item href={`#/action-${index}`} key={index} className="text-white menu-item">
                                    {result.name}
                                </Dropdown.Item>
                            ))}
                        </Dropdown.Menu>

                    </Dropdown>
                </div>
            </div>
            
        </div>
    );
}

export default Search;
