import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import './songdetail.css';
import Song from "../songs/songs";
import { Row, Col } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function Songdetail() {



    const [isPlaying, setIsPlaying] = useState(false);

    const togglePlay = () => {
        const music = document.getElementById('music');
        if (isPlaying) {
            music.pause();
        } else {
            music.play();
        }
        setIsPlaying(!isPlaying);
    };
    const [song, setSongs] = useState([]);

    useEffect(() => {
        const fetchData = async (id) => {
            try {
                let header = new Headers();
                header.append("Content-type", 'application/json');
                let token = localStorage.getItem('token');
                if (token) {
                    header.append('Authorization', token);
                }
                const requestOptions = {
                    method: 'GET',
                    headers: header
                };

                const response = await fetch(`http://localhost:8000/song/${id}/`, requestOptions);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSongs(data);
                console.log(data);
            } catch (error) {
                console.error('Error:', error);
            }
        };
        fetchData(2);
    }, []);

    return (
        <Row className="play-div">
            <Col lg="4" className="song-title">
                <img src={song.image} className="song-image rounded" alt="" />
                <h3 className="title ms-2 text-white">{song.name}</h3>
            </Col>
            <Col lg="4">
                <div className="song">
                    {song.song_file && (
                        <div className="audio-player">
                            <audio controls={true}>
                                <source src={song.song_file} />
                            </audio>
                        </div>
                    )}
                </div>
            </Col>
            <Col lg="4" className="text-end song-button">
                Fon
            </Col>
        </Row>

    );
}



export default Songdetail;
