import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import React, { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
import { Row, Col } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Offcanvas from 'react-bootstrap/Offcanvas';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleDoubleUp, faBars, faHeart, faMusic, faPlay, faSearch } from '@fortawesome/free-solid-svg-icons';
import { faHome } from '@fortawesome/free-solid-svg-icons';
import Login from '../login/login';
import Song from '../songs/songs';
import Songdetail from '../songdetail/songdetail';
import Playlist from '../playlist/playlist';

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
} from "react-router-dom";

import './navbar.css';
import Search from '../search/search';
import Playlistdetail from '../playlistdetail/playlistdetail';
import Liked from '../liked/liked';
function NavBar() {

  const [user, setUser] = useState([]);

  let token = localStorage.getItem('token');


  const fetchUserdata = async () => {
    try {
      let header = new Headers();
      header.append("Content-type", 'application/json');
      let token = localStorage.getItem('token');
      if (token) {
        header.append('Authorization', token);
      }
      const requestOptions = {
        method: 'GET',
        headers: header
      };
      const response = await fetch(`http://localhost:8000/detail/userdetail/`, requestOptions);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setUser(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };
  useEffect(() => {
    if (token){
      
    }
  }, []);
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [song, setSongs] = useState([]);

  const [id, setData] = useState()

  const sendData = (data) => {
    setData(data)
  }



  return (
    <div>
      <Router>
        <Navbar expand="lg" className="nav">
          <Navbar.Brand href="#home" className='ms-5 text-white'><FontAwesomeIcon className='me-3' icon={faMusic}></FontAwesomeIcon><strong>Musicia</strong></Navbar.Brand>
          <Navbar.Toggle className='toggle'>
            <Button variant='btn' onClick={handleShow}>
              <FontAwesomeIcon icon={faBars}></FontAwesomeIcon>
            </Button>
          </Navbar.Toggle>
        </Navbar>
        <Row className='main'>
          <Col lg="4" className="d-none d-lg-block">
            <nav id="sidebar" className='rounded mt-3 ms-3'>
              <ul className="list-unstyled components ms-3">
                <li className="active">
                  <Link to="/song"><FontAwesomeIcon className='me-3' icon={faHome}></FontAwesomeIcon><strong>Home</strong></Link>
                </li>
                <li className="active">
                  <Link to="/search"><FontAwesomeIcon className='me-3' icon={faSearch}></FontAwesomeIcon><strong>Search</strong></Link>
                </li>
              </ul>
            </nav>
            <nav id="sidebar" className='rounded mt-3 ms-3 nav2'>
              <ul className="list-unstyled components ms-3">
                <li>
                  <Link to="/playlist"><FontAwesomeIcon className='me-3' icon={faPlay}></FontAwesomeIcon><strong>Playlists</strong></Link>
                </li>
                <li>
                  <Link to="/liked"><FontAwesomeIcon className='me-3' icon={faHeart}></FontAwesomeIcon><strong>Liked</strong></Link>
                </li>

                {!token ? (
                  <div className='user'>
                    <Button variant='outline-light'>
                      <Link to="/login">Login</Link>
                    </Button>
                    <Button variant='outline-light' className='ms-4'>Create Account</Button>
                  </div>
                ) : (
                  <div>{user.first_name}</div>
                )}

              </ul>
            </nav>
          </Col>
          <Col lg="8" className='rounded cont mt-3'>
            <Routes>
              <Route
                path="/song"
                element={<Song sendData={sendData} />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/search"
                element={<Search />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/playlist"
                element={<Playlist />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/playlistdetail/:id"
                element={<Playlistdetail sendData={sendData} />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/liked"
                element={<Liked sendData={sendData} />}
              ></Route>
            </Routes>
            <Routes>
              <Route
                path="/login"
                element={<Login />}
              ></Route>
            </Routes>
          </Col>
        </Row>
        <div className='play fixed-bottom'>
          <Songdetail idtopass={id} />
        </div>

        <Offcanvas show={show} onHide={handleClose}>
          <Offcanvas.Header className='collapse-title' closeButton>
            <Offcanvas.Title><FontAwesomeIcon className='me-3' icon={faMusic}></FontAwesomeIcon><strong>Musicia</strong></Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body className='off'>
            <nav id="sidebar" className='rounded mt-3 ms-3'>
              <ul className="list-unstyled components ms-3">
                <li className="active" onClick={() => handleClose()}>
                  <Link to="/song"><FontAwesomeIcon className='me-3' icon={faHome}></FontAwesomeIcon><strong>Home</strong></Link>
                </li>
                <li className="active" onClick={() => handleClose()}>
                  <Link to="/search"><FontAwesomeIcon className='me-3' icon={faSearch}></FontAwesomeIcon><strong>Search</strong></Link>
                </li>
                <li onClick={() => handleClose()}>
                  <Link to="/playlist"><FontAwesomeIcon className='me-3' icon={faPlay}></FontAwesomeIcon><strong>Playlists</strong></Link>
                </li>
                <li onClick={() => handleClose()}>
                  <a href="#"><FontAwesomeIcon className='me-3' icon={faHeart}></FontAwesomeIcon><strong>Liked</strong></a>
                </li>
                <li className='mt-auto' onClick={() => handleClose()}>
                  <Link to="/login">Login</Link>
                </li>
              </ul>
            </nav>
          </Offcanvas.Body>
        </Offcanvas>
      </Router>
    </div>

  );
}

export default NavBar;
