import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import './songdetail.css';
import { Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAnglesUp, faBackward, faBars, faForward, faHeart, faPlus, faUpDown } from "@fortawesome/free-solid-svg-icons";
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Offcanvas from 'react-bootstrap/Offcanvas';

function Songdetail({ idtopass }) {
    const [song, setSong] = useState({});
    const [playId, setId] = useState({});
    const [playlist, setPlaylist] = useState([]);
    const audioRef = useRef(null);
    const [isWideScreen, setIsWideScreen] = useState(window.innerWidth >= 992);


    const fetchDataDetail = async (id) => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/song/${id}/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setSong(data);
            var audio = document.getElementById("myAudio");
            audio.src = data.song_file;
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const fetchPlaylist = async () => {
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };

            const response = await fetch(`http://localhost:8000/playlist/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setPlaylist(data);
        } catch (error) {
            console.error('Error:', error);
        }
    };


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => {
        setShow(true);
        fetchPlaylist()
    }


    useEffect(() => {
        if (idtopass) {
            fetchDataDetail(idtopass);
            setId(idtopass)
        }
        // var audio = document.getElementById("myAudio");
        // var currentSongIndex = 0;

    }, [idtopass]);
    const nextPlay = (opt) => {
        if (opt === 'pre') {
            setId(playId - 1)

        } else if (opt === 'nxe') {
            setId(playId + 1)
        }
        else if (playId < 1) {
            playId = 1;
        }

        fetchDataDetail(playId);

    }

    const [show1, setShow1] = useState(false);

    const handleClose1 = () => setShow1(false);
    const handleShow1 = () => setShow1(true);




    return (
        <div>
            <div className="play-main-div">
                {isWideScreen && (
                <Row className="play-div">
                    <Col lg="4" md="12" className="song-title">
                        <img src={song.image} className="song-image rounded ms-2" alt="" />
                        <h3 className="title ms-2 text-white">{song.name}</h3>
                    </Col>
                    <Col lg="4" md="12">
                        <div className="song">
                            <button className="icon-button btn" onClick={() => nextPlay('pre')}><FontAwesomeIcon icon={faBackward} className="icon me-3"></FontAwesomeIcon></button>
                            <div className="audio-player">
                                <audio id="myAudio" ref={audioRef} controls autoPlay className="spotify-audio-player">
                                    <source src="" />
                                </audio>
                            </div>

                            <button className="icon-button btn" onClick={() => nextPlay('nxe')}><FontAwesomeIcon icon={faForward} className="icon ms-3"></FontAwesomeIcon></button>
                        </div>
                    </Col>
                    <Col lg="4" md="12" className="text-end song-button">
                        <div className="me-5">
                            <OverlayTrigger
                                placement="top"
                                delay={{ show: 250, hide: 400 }}
                                overlay={<Tooltip id="button-tooltip">Like</Tooltip>}
                            >
                                <button className="icon-button btn">
                                    <FontAwesomeIcon className="icon m-3" icon={faHeart} />
                                </button>
                            </OverlayTrigger>
                            <OverlayTrigger
                                placement="top"
                                delay={{ show: 250, hide: 400 }}
                                overlay={<Tooltip id="button-tooltip">Add to Playlist</Tooltip>}
                            >
                                <button className="icon-button btn" onClick={handleShow}>
                                    <FontAwesomeIcon className="icon m-3" icon={faPlus} />
                                </button>
                            </OverlayTrigger>
                            <OverlayTrigger
                                placement="top"
                                delay={{ show: 250, hide: 400 }}
                                overlay={<Tooltip id="button-tooltip">More Options</Tooltip>}
                            >
                                <button className="icon-button btn">
                                    <FontAwesomeIcon className="icon m-3" icon={faBars} />
                                </button>
                            </OverlayTrigger>
                        </div>
                    </Col>
                </Row>
                )}
                <Modal show={show} onHide={handleClose} aria-labelledby="contained-modal-title-vcenter"
                    centered>
                    <div className="playlist-modal">
                        <Modal.Header>
                            <Modal.Title className="text-white">Playlists</Modal.Title>
                        </Modal.Header>
                        <Modal.Body className="text-white">
                            {playlist.map((play, index) => (
                                <button className="btn btn-outline-light m-4" key={index}>{play.playlist_name}</button>
                            ))}
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" onClick={handleClose}>
                                Save Changes
                            </Button>
                        </Modal.Footer>
                    </div>
                </Modal>
            </div>
            <div className="collapse-play fixed-bottom">
                <Button variant="btn" className="mb-3" onClick={handleShow1}>
                    <FontAwesomeIcon className="icon" icon={faAnglesUp}></FontAwesomeIcon>
                </Button>

                <Offcanvas show={show1} onHide={handleClose1} placement="bottom" backdrop="false" className="offcanvar-body">
                    <Offcanvas.Header cla closeButton/>
                    <Offcanvas.Body>
                        Some text as placeholder. In real life you can have the elements you
                        have chosen. Like, text, images, lists, etc.
                    </Offcanvas.Body>
                </Offcanvas>
            </div>
        </div>
    );
}

export default Songdetail;
