import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function Playlistdetail() {
    const { id } = useParams(); // Extracting id from useParams()

    const [songs, setSongs] = useState([]);

    const fetchPlaylistData = async (playlistId) => { // Renaming the parameter to playlistId to avoid confusion
        try {
            let header = new Headers();
            header.append("Content-type", 'application/json');
            let token = localStorage.getItem('token');
            if (token) {
                header.append('Authorization', token);
            }
            const requestOptions = {
                method: 'GET',
                headers: header
            };
            const response = await fetch(`http://localhost:8000/playlist/${playlistId}/listplaylistsong/`, requestOptions);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setSong(data);
            var audio = document.getElementById("myAudio");
            audio.src = data.song_file;
        } catch (error) {
            console.error('Error:', error);
        }
    };


    useEffect(() => {
        fetchPlaylistData(id);
    }, [id]); // Adding id to the dependency array to re-fetch data when id changes

    return (
        <div>
            <h1 className="text-white">Hello</h1>
        </div>
    );
}

export default Playlistdetail;
